import { useState } from 'react'
import {
    HStack,
    Tag,
    TagLabel,
    TagCloseButton,
    Box
} from '@chakra-ui/react'

export default function Filter(props: any) {
    const [typesList, setTypesList] = useState<string[]>([])
    const [colorsList, setColorsList] = useState<string[]>([])

    // handles the filter parameters
    const addType = (type: string) => {
        // if type is already in list, remove type from list
        if (typesList.includes(type))
        {
            let index = -1
            for (let i = 0; i < typesList.length; i++)
            {
                if (typesList[i] == type)
                    index = i
            }

            typesList.splice(index, 1); // 2nd parameter means remove one item only
        }
        // else add type to list
        else 
        {
            typesList.push(type)
        }

        if (typesList.length == 0)
        {
            props.setTypes("")
        }

        // turn types list into string with items separated by a comma
        props.setTypes(typesList.toString())
        console.log(typesList)
        console.log(props.types)
    }

    // show different variant for tags to indicate if it is checked or not
    const getVariantType = (type: string) => {
        if (typesList.includes(type))
            return "subtle"
        else
            return "outline"
    }

    return (
        <>
            <HStack spacing={8} m={10}>
            {['Dress', 'Jacket', 'Top', 'Pants', 'Shorts', 'Skirt', 'Sweater', 'Shoe'].map((type) => (
                <Tag
                size='lg'
                key={type}
                borderRadius='full'
                variant={getVariantType(type)}
                colorScheme='red'
                onClick={() => addType(type)}
                >
                    <TagLabel>{type}</TagLabel>
                </Tag>
            ))}
            </HStack>
            <HStack spacing={4} m={10}>
            {['red', 'burgundy', 'orange', 'yellow', 'green', 'blue', 'light blue', 'purple', 'pink', 'beige', 'brown', 'gray', 'white'].map((type) => (
                <Tag
                size='lg'
                key={type}
                borderRadius='full'
                variant={getVariantType(type)}
                colorScheme='red'
                onClick={() => addType(type)}
                >
                    <TagLabel>{type}</TagLabel>
                </Tag>
            ))}
            </HStack>
        </>
    )
}
