import { useState, useRef } from 'react'
import {
    Center,
    Heading,
    Box,
    Drawer,
    DrawerOverlay,
    DrawerBody,
    DrawerContent,
    SimpleGrid,
    useDisclosure,
    DrawerHeader,
    IconButton,
    Icon,
    useToast
  } from '@chakra-ui/react';
  import {
    HamburgerIcon,
    AddIcon,
    ChevronDownIcon,
    ChevronRightIcon,
  } from '@chakra-ui/icons';
  import { AiOutlinePlusCircle } from 'react-icons/ai'
  import ClothingModal from "./ClothingModal";

  export default function NavBar() {
    const { isOpen, onOpen, onClose } = useDisclosure()
    const inputFile = useRef<HTMLInputElement | null>(null)
    //const [fileName, setFileName] = useState<string | null>(null)
    const toast = useToast()
    const { isOpen: isModalOpen, onOpen: onModalOpen, onClose: onModalClose } = useDisclosure()
    const [clothing, setClothing] = useState<any>(null)

    const onAddClick = () => {
      inputFile.current?.click();
    }

    const onChangeFile = (event: any) => {
      event.stopPropagation();
      event.preventDefault();
      let fileUpload = event.target.files[0];
      let fileName = fileUpload.name
      //console.log(fileUpload.name);
      //setFileName(fileUpload.name); 

      console.log(fileName)

      const requestOptions = {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ path: fileName })
      };

      console.log("file name: " + fileName)

      fetch(`http://127.0.0.1:8000/api/closet/add`, requestOptions).then((response) => {
        return response.json();
      }).then((result) => {
        //console.log(result)
        toast({
          title: 'Clothing item added.',
          status: 'success',
          duration: 8000,
          isClosable: true,
        })
        onModalOpen
        setClothing(result)
      });
    }

    return (
        <>
          <ClothingModal isOpen={isModalOpen} onOpen={onModalOpen} onClose={onModalClose} clothing={clothing}/>
          <input type='file' id='file' accept="image/*" ref={inputFile} style={{display: 'none'}} onChange={onChangeFile}/>
          <SimpleGrid columns={2} spacing='0px' mb={6}>
              <Box bg='red.400' height='100px'>
                <Heading as='h2' size='2xl' color="white">
                  <IconButton mt={7} ml={6} aria-label='Menu' icon={<HamburgerIcon w={10} h={10} />} bg='red.400' color='white' onClick={onOpen}/>
                </Heading>
              </Box>
              <Box bg='red.400' height='100px'>
                <Heading mt={6} mr={8} textAlign={"right"} as='h2' size='2xl' color="white">
                  DigiCloset
                </Heading>
              </Box>
          </SimpleGrid>
          <Drawer placement='left' onClose={onClose} isOpen={isOpen}>
            <DrawerOverlay />
            <DrawerContent>
              <DrawerHeader borderBottomWidth='1px'>Menu</DrawerHeader>
              <DrawerBody>
                <p>Closet</p>
                <p>OOTD</p>
              </DrawerBody>
            </DrawerContent>
          </Drawer>
          <Box position='fixed'
                bottom='40px'
                right={['16px', '40px']}
                zIndex={1}
            >
              <IconButton bg='transparent' aria-label='Menu' fontSize='50px' color='red.400' icon={<AddIcon />} onClick={onAddClick} />
            </Box>
        </>
  )
}
