// Importing combination
import { useEffect, useState, useRef } from "react";
import React from 'react'
import { 
    Tag, 
    Card, 
    CardHeader, 
    CardBody, 
    CardFooter,
    Image ,
    Stack,
    Heading,
    Divider,
    ButtonGroup,
    Button,
    SimpleGrid,
    Box,
    IconButton,
    AlertDialog,
    AlertDialogBody,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogContent,
    AlertDialogOverlay,
    useDisclosure,
    useToast
} from '@chakra-ui/react'
import {DeleteIcon} from '@chakra-ui/icons'
import NavBar from '../components/NavBar'
import ClothingModal from '../components/ClothingModal'
import {AiFillEdit} from 'react-icons/ai'

export function Closet(props: any) {
    const [closet, setCloset] = useState([]);
    const [deleteStatus, setDeleteStatus] = useState("");
    const { isOpen, onOpen, onClose } = useDisclosure()
    const cancelRef = useRef<HTMLButtonElement>(null);
    const [colors, setColors] = useState([])
    const toast = useToast()
    const { isOpen: isModalOpen, onOpen: onModalOpen, onClose: onModalClose } = useDisclosure()
    const [clothing, setClothing] = useState<any>(null)

    useEffect(() => {
        async function getCloset() {
          const response = await fetch(
            `http://127.0.0.1:8000/api/closet`
          )

          setCloset(await response.json())
        }
        getCloset()
      }, [closet])

    const handleDelete = (c: any) =>
    {
        const requestOptions = {
            method: 'POST'
          };

          // Note: I'm using arrow functions inside the `.fetch()` method.
          // This makes it so you don't have to bind component functions like `setState`
          // to the component.
          fetch(`http://127.0.0.1:8000/api/closet/${c.id}/delete`, requestOptions).then((response) => {
          }).then((result) => {
            onClose()
            toast({
                title: 'Clothing item deleted.',
                status: 'success',
                duration: 5000,
                isClosable: true,
              })
          });
    }

    //console.log(isOpen)

    const getColors = (c: any) =>
    {
        return c.colors?.split(",")
    }

    return (
        <div>
            <NavBar />
            <ClothingModal isOpen={isModalOpen} onOpen={onModalOpen} onClose={onModalClose} clothing={clothing}/>
            <SimpleGrid columns={3} spacing={2}>
                {closet.map((c: any) => (
                    <Box w='100%' h='100%' key={c.id}>
                        <Card maxW='sm' key={c.id}>      
                            <CardBody>
                                <Image
                                src={"/" + c.image}
                                alt={"Clothing " + c.id}
                                borderRadius='lg'
                                />
                            </CardBody>
                            <Divider color='gray'/>
                            <CardFooter>
                                <ButtonGroup spacing='2'>
                                    <Tag size='md' key={c.id + "." + c.type} variant='solid' bg='orange.200'>
                                        {c.type}
                                    </Tag>
                                    {getColors(c).map((color: string | null) => color && (
                                        <Tag size='md' key={c.id + "." + color} variant='solid' bg='red.700'>
                                            {color}
                                        </Tag>
                                    ))}
                                    <IconButton mr={6} aria-label='Delete Clothing' icon={<DeleteIcon />} bg='transparent' color='gray' onClick={onOpen}/>
                                    <IconButton mr={6} aria-label='Edit Clothing' icon={<AiFillEdit />} bg='transparent' color='gray' onClick={() => {
                                        setClothing(c)
                                        onModalOpen()
                                    }}/>
                                </ButtonGroup>
                            </CardFooter>
                        </Card>
                        <AlertDialog
                            isOpen={isOpen}
                            leastDestructiveRef={cancelRef}
                            onClose={onClose}
                        >
                            <AlertDialogOverlay>
                                <AlertDialogContent>
                                    <AlertDialogHeader fontSize='lg' fontWeight='bold'>
                                    Delete Clothing
                                    </AlertDialogHeader>

                                    <AlertDialogBody>
                                    Are you sure? You can't undo this action afterwards.
                                    </AlertDialogBody>

                                    <AlertDialogFooter>
                                    <Button ref={cancelRef} onClick={onClose}>
                                        Cancel
                                    </Button>
                                    <Button bg='red.400' color='white' onClick={() => handleDelete(c)} ml={3}>
                                        Delete
                                    </Button>
                                    </AlertDialogFooter>
                                </AlertDialogContent>
                            </AlertDialogOverlay>
                        </AlertDialog>
                    </Box>
                ))}
            </SimpleGrid>
        </div>
    );
}
