import { useState } from 'react'
import NavBar from "../components/NavBar"
import TemperatureContainer from "../components/TemperatureContainer"
import {
    Box,
    Text,
    Card,
    CardBody,
    Image
} from "@chakra-ui/react"

export function OOTD(props: any) {
    const [outfit, setOutfit] = useState<any | null>(null)
    const [missing, setMissing] = useState<any | null>(null)

    const getOOTD = async () =>
    {
        const response = await fetch(
            `http://127.0.0.1:8000/api/ootd`
          )

        setOutfit(await response.json())

        if (outfit?.outfit != undefined)
        {
            setOutfit(outfit.outfit)
            setMissing(null)
        }

        else if (outfit?.missing != undefined)
        {
            setMissing(outfit.missing)
            setOutfit(null)
        }

        console.log(outfit)
        console.log(missing)

    }

    return (
        <>
            <NavBar />
            <TemperatureContainer />
            <Box 
                as='button'
                borderRadius='md'
                bg='red.300'
                color="white"
                px={6} 
                h={14}
                position='fixed'
                bottom='50px'
                right={["50%", "45%"]}
                onClick={getOOTD}
            >
                <Text fontSize="3xl" mt={2}>Generate</Text>
                {outfit && outfit?.map((clothing: any) => (
                    <>
                        <Card maxW='sm' key={clothing}>      
                            <CardBody>
                                <Image
                                src={"/" + clothing}
                                alt={clothing}
                                borderRadius='lg'
                                />
                            </CardBody>
                        </Card>
                    </>
                ))}
            </Box>
        </>
    )
}
