import { useEffect, useState, useRef } from 'react'
import NavBar from "../components/NavBar"
import TemperatureContainer from "../components/TemperatureContainer"
import {
    Box,
    Text,
    Card,
    CardBody,
    Image
} from "@chakra-ui/react"

export function OOTD(props: any) {
    const [data, setData] = useState<any>(null)
    const [outfit, setOutfit] = useState<string[] | null>()
    const [missing, setMissing] = useState<string | null>("")

    const ChipStyles = useRef({
        top: Math.floor(Math.random()*50),
        left: '50%',
        transform: 'translate(-50%, -50%)'
    });

    useEffect(() => {
      }, [outfit, missing])

    const getOOTD = async () =>
    {
        const response = await fetch(
            `http://127.0.0.1:8000/api/ootd`
          )

          setData(await response.json())

        if (data?.outfit != undefined)
        {
            setOutfit(data.outfit)
            setMissing(null)
        }

        else if (data?.missing != undefined)
        {
            setMissing(data.missing)
            setOutfit(null)
        }
    }

    return (
        <>
            <NavBar />
            <TemperatureContainer />
            {outfit && outfit?.map((clothing: any) => (
                <div >
              <Image
                            src={"/" + clothing}
                            alt={clothing}
                            borderRadius='lg'
                            style={ChipStyles.current}
                        />
            </div>

            ))}
            <Box 
                as='button'
                borderRadius='md'
                bg='red.300'
                color="white"
                px={6} 
                h={14}
                position='fixed'
                bottom='50px'
                right={["50%", "45%"]}
                onClick={getOOTD}
            >
                <Text fontSize="3xl" mt={2}>Generate</Text>
            </Box>
        </>
    )
}
