// Importing combination
import { useEffect, useState } from "react";
import { 
    Text, 
    Card, 
    CardHeader, 
    CardBody, 
    CardFooter,
    Image ,
    Stack,
    Heading,
    Divider,
    ButtonGroup,
    Button,
    SimpleGrid,
    Box
} from '@chakra-ui/react'
import {DeleteIcon} from '@chakra-ui/icons'

export function LandingPage(props: any) {
    const [closet, setCloset] = useState([]);

    useEffect(() => {
        async function getCloset() {
          const response = await fetch(
            `http://127.0.0.1:8000/api/closet`
          )

          setCloset(await response.json())
        }
        getCloset()
      }, [closet])

    return (
        <div>
            <SimpleGrid columns={3} spacing={2}>
                {closet.map((c: any) => (
                    <Box bg='tomato' w='100%' h='100%'>
                        <Card maxW='sm' key={c.id}>      
                            <CardBody>
                                <Image
                                src={"/" + c.image}
                                alt='Green double couch with wooden legs'
                                borderRadius='lg'
                                />
                            </CardBody>
                            <Divider />
                            <CardFooter>
                                <ButtonGroup spacing='2'>
                                    <DeleteIcon />
                                </ButtonGroup>
                            </CardFooter>
                        </Card>
                    </Box>
                ))}
            </SimpleGrid>
        </div>
    );
}
