// Importing combination
import { useEffect, useState } from "react";
import { 
    Text, 
    Card, 
    CardHeader, 
    CardBody, 
    CardFooter,
    Image ,
    Stack,
    Heading,
    Divider,
    ButtonGroup,
    Button,
    SimpleGrid,
    Box,
    IconButton
} from '@chakra-ui/react'
import {DeleteIcon} from '@chakra-ui/icons'

export function LandingPage(props: any) {
    const [closet, setCloset] = useState([]);
    const [deleteStatus, setDeleteStatus] = useState("");

    useEffect(() => {
        async function getCloset() {
          const response = await fetch(
            `http://127.0.0.1:8000/api/closet`
          )

          setCloset(await response.json())
        }
        getCloset()
      }, [closet])

    const handleDelete = (c: any) =>
    {
        useEffect(() => {
            // DELETE request using fetch inside useEffect React hook
            fetch('http://127.0.0.1:8000/api/closet/${c.id}/delete', { method: 'POST' })
                .then(() => setDeleteStatus('Delete successful'));

        // empty dependency array means this effect will only run once (like componentDidMount in classes)
        }, []);
    }

    return (
        <div>
            <SimpleGrid columns={3} spacing={2}>
                {closet.map((c: any) => (
                    <Box w='100%' h='100%'>
                        <Card maxW='sm' key={c.id}>      
                            <CardBody>
                                <Image
                                src={"/" + c.image}
                                alt={"Clothing " + c.id}
                                borderRadius='lg'
                                />
                            </CardBody>
                            <Divider />
                            <CardFooter>
                                <ButtonGroup spacing='2'>
                                    <IconButton aria-label='Delete Clothing' icon={<DeleteIcon />} onClick={() => handleDelete(c)}/>
                                </ButtonGroup>
                            </CardFooter>
                        </Card>
                    </Box>
                ))}
            </SimpleGrid>
        </div>
    );
}
