import { useEffect, useState } from 'react'
import NavBar from "../components/NavBar"
import TemperatureContainer from "../components/TemperatureContainer"
import {
    Box,
    Text,
    Card,
    CardBody,
    Image,
    Grid,
    GridItem,
    Stack
} from "@chakra-ui/react"

export function OOTD(props: any) {
    const [data, setData] = useState<any>(null)
    const [outfit, setOutfit] = useState<string[] | null>(null)
    const [missing, setMissing] = useState<string | null>(null)

    useEffect(() => {
      }, [outfit, missing])

    const getOOTD = async () =>
    {
        const response = await fetch(
            `http://127.0.0.1:8000/api/ootd`
          )

          setData(await response.json())

        if (data?.outfit != undefined)
        {
            setOutfit(data.outfit)
            setMissing(null)
        }

        else if (data?.missing != undefined)
        {
            setMissing(data.missing)
            setOutfit(null)
        }
    }

    return (
        <>
            <NavBar />
            <TemperatureContainer />
                <Grid
                    h='100%'
                    templateRows='repeat(2, 1fr)'
                    templateColumns='repeat(5, 1fr)'
                    gap={4}
                >
                                {outfit && outfit?.map((clothing: any) => (

                    <GridItem rowSpan={1} colSpan={1} bg='tomato'>
                        <Card maxW='sm' key={clothing}>      
                            <CardBody key={clothing}>
                                <Image
                                    src={"/" + clothing}
                                    alt={clothing}
                                    borderRadius='lg'
                                />
                            </CardBody>
                        </Card>
                    </GridItem>
            ))}
                            </Grid>

            <Box 
                as='button'
                borderRadius='md'
                bg='red.300'
                color="white"
                px={6} 
                h={14}
                position='fixed'
                bottom='50px'
                right={["50%", "45%"]}
                onClick={getOOTD}
            >
                <Text fontSize="3xl" mt={2}>Generate</Text>
            </Box>
        </>
    )
}
