// Importing combination
import { useEffect, useState, useRef } from "react";
import { 
    Text, 
    Card, 
    CardHeader, 
    CardBody, 
    CardFooter,
    Image ,
    Stack,
    Heading,
    Divider,
    ButtonGroup,
    Button,
    SimpleGrid,
    Box,
    IconButton,
    AlertDialog,
    AlertDialogBody,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogContent,
    AlertDialogOverlay,
} from '@chakra-ui/react'
import {DeleteIcon} from '@chakra-ui/icons'

export function LandingPage(props: any) {
    const [closet, setCloset] = useState([]);
    const [deleteStatus, setDeleteStatus] = useState("");
    const [isDeleteOpen, setIsDeleteOpen] = useState(false);
    const cancelRef = useRef()

    useEffect(() => {
        async function getCloset() {
          const response = await fetch(
            `http://127.0.0.1:8000/api/closet`
          )

          setCloset(await response.json())
        }
        getCloset()
      }, [closet])

    const handleDelete = (c: any) =>
    {
        const requestOptions = {
            method: 'POST'
          };

          // Note: I'm using arrow functions inside the `.fetch()` method.
          // This makes it so you don't have to bind component functions like `setState`
          // to the component.
          fetch(`http://127.0.0.1:8000/api/closet/${c.id}/delete`, requestOptions).then((response) => {
            return response.json();
          }).then((result) => {
            // do what you want with the response here
          });
    }

    return (
        <div>
            <SimpleGrid columns={3} spacing={2}>
                {closet.map((c: any) => (
                    <Box w='100%' h='100%'>
                        <Card maxW='sm' key={c.id}>      
                            <CardBody>
                                <Image
                                src={"/" + c.image}
                                alt={"Clothing " + c.id}
                                borderRadius='lg'
                                />
                            </CardBody>
                            <Divider />
                            <CardFooter>
                                <ButtonGroup spacing='2'>
                                    <IconButton aria-label='Delete Clothing' icon={<DeleteIcon />} onClick={() => setIsDeleteOpen(true)}/>
                                </ButtonGroup>
                            </CardFooter>
                        </Card>
                    </Box>
                ))}
            </SimpleGrid>
            <AlertDialog
                isOpen={isDeleteOpen}
                leastDestructiveRef={null}
                onClose={() => setIsDeleteOpen(false)}
            >
                <AlertDialogOverlay>
                    <AlertDialogContent>
                        <AlertDialogHeader fontSize='lg' fontWeight='bold'>
                        Delete Customer
                        </AlertDialogHeader>

                        <AlertDialogBody>
                        Are you sure? You can't undo this action afterwards.
                        </AlertDialogBody>

                        <AlertDialogFooter>
                            <Button onClick={() => setIsDeleteOpen(false)}>
                                Cancel
                            </Button>
                            <Button colorScheme='red' onClick={() => setIsDeleteOpen(false)} ml={3}>
                                Delete
                            </Button>
                        </AlertDialogFooter>
                    </AlertDialogContent>
                </AlertDialogOverlay>
            </AlertDialog>
        </div>
    );
}
